const SECRET_KEY = process.env.JWT_SECRET_KEY || '{000-1-00232-111-931313-13142-12129-24-3333}'; // TEMP
const jwt = new JWT(SECRET_KEY);

const { appTokenAttribute } = require('../config');

const attributesDict = {
  [appTokenAttribute]: 'userInfo',
  authorization: 'authorizationInfo',
};

module.exports = class AuthenticationProvider {
  constructor(authData, authenticationAttributes) {
    this.authData = authData;
    this.authenticationAttributes = authenticationAttributes;
  }

  isValid() {
    try {
      this.authenticationAttributes.forEach((attribute) => {
        const jwtToken = this.authData && this.authData[attribute];

        jwt.verify(jwtToken, SECRET_KEY);
      });

      return true;
    } catch (error) {
      return false;
    }
  }

  getAuthorization() {
    const authorization = {};

    Object.keys(attributesDict).forEach((attribute) => {
      if (this.authData[attribute]) {
        const tokenData = jwt.decode(this.authData[attribute]);

        delete tokenData.iat;
        authorization[attributesDict[attribute]] = tokenData;
      }
    });

    return authorization;
  }
};

const humps = require('humps');
const { JWT } = require('../security');
const { error } = require('./authorization-enum');


const SECRET_KEY = process.env.JWT_SECRET_KEY || '{000-1-00232-111-931313-13142-12129-24-3333}';


const jsonwebtoken = new JWT(SECRET_KEY);

/**
 *  @constructor
 *  @param {Object} headers Headers received in the application, which contains token's
 *  @param {jsonwebtoken} auth Authentication Method instantiated object, default's to JWT.
 */
class DynamicAuthenticationProdiver {
  constructor({ headers = {}, auth = jsonwebtoken }) {
    this.headers = this.setHeadersToCamelCase(headers);
    this.attributtes = [];
    this.auth = auth;
  }

  /**
   * @private
   */
  setHeadersToCamelCase(headers) {
    return humps.camelizeKeys(headers);
  }

  setHeadersToBeDecrypted(/* istanbul ignore next */ headerAttributes = []) {
    headerAttributes.forEach(({ header, requestProp }) => {
      this.attributtes.push({ header: humps.camelize(header), requestProp });
    });
  }

  /**
   * @private
   * @param {String} token
   */
  decryptToken(token) {
    try {
      this.auth.verify(token);
      const decoded = this.auth.decode(token);

      delete decoded.iat;

      return decoded;
    } catch (exception) {
      throw new Error(exception);
    }
  }

  /**
   * @private
   */
  assignAuthenticatedToRequestProp(headers, attributtes) {
    const validatedTokens = {};

    Object.keys(headers).forEach((header) => {
      attributtes.forEach((attribute) => {
        if (header === attribute.header) {
          validatedTokens[[attribute.requestProp]] = this.decryptToken(headers[header]);
        }
      });
    });

    return validatedTokens;
  }

  /**
   * @private
   */
  getAuthorization() {
    const validatedTokens = this.assignAuthenticatedToRequestProp(this.headers, this.attributtes);

    if (Object.keys(validatedTokens).length === 0) {
      throw new Error(error.noAuthProduced);
    }

    return validatedTokens;
  }
}

module.exports = DynamicAuthenticationProdiver;

const jwt = require('jsonwebtoken');

const defaultOptions = Object.freeze({
  expiresIn: '1d',
});

class JWT {
  constructor(secretKey) {
    this.secretKey = secretKey;
  }

  decode(token) {
    return jwt.decode(token);
  }

  verify(token) {
    return jwt.verify(token, this.secretKey);
  }

  sign(payload, options = {}) {
    return jwt.sign(payload, this.secretKey, { ...defaultOptions, ...options });
  }
}

module.exports = {
  JWT,
};

const { BaseCommand } = require('architecture-code-structure');

/**
 * A command to use in your tests to fake an event and the data within the event
 * @extends {BaseCommand}
 */
class FakeCommand extends BaseCommand {
  /**
  * @param {string} eventName - the event name that will be fired
  * @param {object} args - the object that will be returned when the event is fired
  */
  constructor(eventName, args) {
    super();
    /**
    * @type {object}
    */
    this.args = args;
    /**
    * @type {string}
    */
    this.eventName = eventName;
  }

  /**
   * emits the event
   */
  execute() {
    this.emit(this.eventName, this.args);
  }
}

/**
 * Creates a factory using the factory pattern with the create method returning the command passed
 * @param {object} - the command object
 * @return {Class} a Class with an create function
 */
function factory(command) {
  return class FakeFactory {
    create() {
      return command;
    }
  };
}

module.exports = {
  factory,
  FakeCommand,
};

/**
 * Enables the mocha done parameter using async/await
 * @param {function(done: function): undefined} - your test function
 * @return {function} a new function receiving the done and passing it to your function
 * @throws {Error} when your function has not argument or your function is not an async one.
 */
function asyncDone(fn) {
  if (fn.length !== 1) {
    throw new Error('The function must receive a single done argument');
  }

  return (done) => {
    const promise = fn(done);

    if (!promise || !promise.then) {
      throw new Error('The function must return a promise');
    }
  };
}

module.exports = {
  asyncDone,
};

const Promise = require('bluebird');

class ArrayUtils {
  constructor(limit = 10) {
    this.limit = limit;
  }

  /* istanbul ignore next */
  prepareArguments(dataset, context, {
    down, up, limit = null, concurrent,
  }, iteree) {
    return {
      concurrent,
      context,
      dataset,
      down,
      iteree,
      limit,
      up,
    };
  }

  verifyLimitAndProp(iteree, item, limit) {
    return (item[[iteree]] !== undefined && (limit !== null && limit > 0));
  }

  /**
    * Recursively iterates over array on a specific iteree and executes a
    * function when the object is being mapped (down) and other when the map is over (up).
    * @param {Object} args - DeepMap function arguments.
    * @param {Array} args.dataset - Dataset to be iterated over.
    * @param {string} args.iteree - Attribute which the recursion occurs.
    * @param {function|Promise} args.down - Function that will be executed when the object is mapped
    * @param {function|Promise} args.up - Function that will be executed when
    * the mapping (down function) is executed
    * @param {object} args.context - Context from previous iteration.
  */

  /* eslint-disable max-lines-per-function */
  /* istanbul ignore next */
  async deepMap({
    dataset, iteree, down = (item, context) => ({ context, item }), up = item => item,
    context, limit = this.limit, concurrent = true,
  }) {
    const promisifiedDataset = await Promise.map(dataset, async (iterated) => {
      const { item, context: newContext } = await down(iterated, context);

      if (this.verifyLimitAndProp(iteree, item, limit)) {
        item[[iteree]] = await this.deepMap(
          this.prepareArguments(item[iteree], newContext, {
            concurrent,
            down,
            limit: limit !== null && limit > 0 ? limit - 1 : null,
            up,
          }, iteree),
        );
      }

      return up(item);
    }, { concurrency: concurrent ? Infinity : 1 });

    return promisifiedDataset;
  }
}
module.exports = ArrayUtils;
